student_answer:
 function computedPrice(car){
    let arr=car.map((item)=>{
        return {
            typeName:item.groupName,
            price:item.type.reduce((a,b)=>{
                return a+b.price
            },0)
    }})
    let allPrice = arr.reduce((a,b)=>{
                return a+b.price
            },0)
    return {
        allPrice,
        group:arr
    }
}

