const fs = require("fs");

function radeDir(path){
    let dirSize = fs.statSync(path,'utf-8').size; //1m=1024kb 1kb=1024b
    let towMb=1024*1024*2//2M

    if(dirSize>=towMb){
        //大文件读取
        //读取文件流
        let rs = fs.createReadStream(path)

        // //写文件流
        // let ws = fs.createWriteStream('./moves/express.mp4')

        //管道
        return rs.pipe()
    }else{
        //小文件读取
        return fs.readFileSync(path,'utf-8')

    }
    // console.log(path)

}
radeDir('./a.txt')

