//定义一个w走位的数据，用于存放x,y的偏移位置
const wPostiton = [
    { x: 0, y: 0 },
    { x: 50, y: 200 },
    { x: 100, y: 0 },
    { x: 150, y: 200 },
    { x: 200, y: 0 }
]
//点击oBox，使用async函数
oBox.onclick = async function() {
    for (let v of wPostiton) {//遍历w走位的数据
