//使用async函数实现多张图片的依次加载（第一张加载完之后才能加载第二张）
        let loadImg = (src) => {
            return new Promise((resolve, reject) => {
                let img = document.createElement('img')
                img.src = src
                document.body.append(img)
                setTimeout(() => {
                    resolve(true)
                }, 1000)
            })
        }
        const imgs = ['images/banner1.jpg', 'images/banner2.jpg', 'images/banner3.jpg', 'images/banner4.jpg'];
        // 依次加载图片
        async function fSync(imgs) {
            for (let i of imgs) {
                await loadImg(i)
            }
        }
