function getRate(ping, zong) {
  var str = '';
  if (zong === undefined) {
    zong = 5;
  }
  if (zong <= 0 || zong < ping) {
    return 'error'
  } else {
    for (var i = 0; i < ping; i++) {
      str += '★'
    }
    for (var i = 0; i < (zong - ping); i++) {
      str += '☆'
    }
    return str
  }
}
