function jumpFloor(number)
{
    if(number===1){
        return 1;
    }
    else if(number===2){
        return 2;
    }

    else{
        var pre = 1;
        var cur = 2;
        for(var i = 2;i<number;i++){
            cur+=pre;
            pre = cur-pre;
        }
        return cur;
    }
}

const fs = require('fs');
class readFile {
    constructor(url) {
        this.url = url;
        this.init()
    }
    init() {
        let size = fs.statSync(this.url).size;//2M = 2097152b  2M=(1024*2)*1024
        console.log(size)
        if (size >2097152) {
            // 默认为大文件
            this.readStream()
        } else {
            // 默认为小文件
            this.readSmallFile()
        }
    }
    readStream() {
        let rs = fs.createReadStream(this.url);// 可读流
        let ws = fs.createWriteStream('./big/' + this.url);
        rs.pipe(ws)
    }
    readSmallFile() {
        fs.readFile(this.url, 'utf-8', (err, con) => {
            if (err) throw err;
            fs.writeFileSync('./small/' + this.url, con)
        })
    }
}
