student_answer:
let loadImg = (src) => {
    return new Promise((resolve, reject) => {
        let img = document.createElement('div')
        img.innerHTML = src
        document.body.append(img)
        setTimeout(() => {
            resolve(true)
        }, 2000)
    })
}
const imgs = ['url1图片一', 'url2图片二', 'url3图片三'];
// 依次加载图片
async function fAsync(imgs) {
    for (let i of imgs) {
        await loadImg(i)
    }
}
fAsync(imgs);

