let loadImg = (src) => {
            return new Promise((resolve, reject) => {
                let img = document.createElement('img')
                img.src = src
                document.body.append(img)
                setTimeout(() => {
                    resolve(true)
                }, 1000)
            })
        }
        const imgs = ['1.jpg', '2.jpg', '3.jpg', '4.jpg'];
        // 依次加载图片
        async function fImg(imgs) {
            for (let i of imgs) {
                await loadImg(i)
            }
        }
        fImg(imgs);
