function frog(res=[],count=0){//默认值
	var i=1;
  //设定i小于2 得到青蛙每次只能跳1或2级台阶
	while(i<3){
      //count为已将跳的台阶数 i是已经跳了的台阶数
      //count+i的值小于等于10的话 继续执行
   		if(count+i<=10){
          //重新指定数组resarr 使数组中的数组只有1和2
      		let resarr=[...res];
          resarr.push(i);
          //如果count+i等于10了后打印数组
          if(count+i==10){
          		console.log(resarr);
          }else{//如果不等于10 则继续执行
          		frog(resarr,count+i);
          }
       }
      i++;
   }
}
