let fs=require("fs");
let path=require("path");
//获取文件信息的绝对路径
let filePath = path.join(__dirname, "./a.js");
function fileSize(filePath) {
    //先打印文件信息 获取到文件的大小
    let filesize = fs.statSync(filePath);
    //使用size算出传入的文件大小
    let res = filesize.size / 1024 / 1024;
    //如果size小于2的话 正确读取文件内容
    if (res < 2) {
      	//readFileSync()的方法读取文件的内容 "utf-8"改变字符内容的编码格式
        let stream = fs.readFileSync(filePath,"utf-8");
        console.log(stream)
    } else {
      		//如果文件大于2M 提示内容不可以上传
        console.log("文件大于2M 不可以上传");
    }
}
//传入路径
fileSize(filePath)

var arr = [1,2,5,[1,[4,5,[8]]]];
//传入多维数组
function array(arr){ 
  //指定一个空数组
  var res = []; 
  //for循环遍历数组的每一项
  for(var i=0;i<arr.length;i++){
    //判断arr中的每一项是不是Array 
    if(Array.isArray(arr[i])){
      //concat()把arr里的值取出来添加到原数组后
      res = res.concat(array(arr[i]));
    }else{
      //push()就把数组添加到原数组后 
      res.push(arr[i]);
    } 
  } 
  return res; 
}
