    return new Promise((resolve, reject) => {
        // write your code here
        let img = document.createEvent('img');
        img.src = src;
        document.body.append(img);
        setTimeout(() => {
            resolve(true)
        }, 1000)
    })
}
const imgs = ['url1', 'url2', 'url3'];
