const mostProductivePigCount  = (dom) => {
  const piglist = [];
  const allChild = [];
  piglist.push(dom)
  while (piglist.length > 0) {
    let size = piglist.length;
    let max = 0;
    while (size--) {
      const pig = piglist.shift();
      max = Math.max(pig.children.length, max)
      piglist.push(...pig.children)
    }
    allChild.push(max)
  }
  return allChild
}
