function check(str) {
            var U = 0,
                D = 0,
                L = 0,
                R = 0;
            for (var i = 0; i < str.length; i++) {
                // console.log(str[i])
                switch (str[i]) {
                    case "U":
                        U++;
                        D--;
                        break;
                    case "D":
                        D++;
                        U--;
                        break;
                    case "L":
                        L++;
                        R--;
                        break;
                    case "R":
                        R++;
                        L--;
                        break;
                }
            }
            return U == 0 && L == 0
        }
