function jumpFloor2(n) {
      var target = 0,
        number1 = 1,
        number2 = 2;

      if (n <= 0) return 0;
      if (n == 1) return 1;
      if (n == 2) return 2;
      for (var i = 3; i <= n; ++i) {
        target = number1 + number2;
        number1 = number2;
        number2 = target;
      }
      return target;
    }

    console.log(jumpFloor2(100)) // 输出 573147844013817200000
    console.log(jumpFloor2(1000)) // 输出 7.0330367711422765e+208
    console.log(jumpFloor2(10000)) //Infinity

const fs = require('fs');
const path = require('path');

function copySmall(originPath, targetPath) {
    fs.readFile(originPath, (err, data) => {
        if (!err) {
            fs.writeFileSync(targetPath, data)
        }
    })
}

function copyBig(originPath, targetPath) {
    let rs = fs.createReadStream(originPath)
    let ws = fs.createWriteStream(targetPath)
    rs.pipe(ws)
}

function copy(originPath, targetPath, size = 2048) {
    fs.stat(originPath, (err, data) => {
        if (!err) {
            if (data.size > size) {
                copySmall(originPath, targetPath)
            } else {
                copyBig(originPath, targetPath)
            }
 }
    })
}

