const fs = require("fs");
class readFile{
	constructor(src){
    	this.src = src;
      this.init()
    }
  init(){
    //获取文件大小
 	  let size = fs.ststSync(this.src).size;
	  console.log(size)//拿到文件大小
    //判断文件是否大于指定值
     if(size>2000){
     	this.readBig()
     }else{
     	this.readSmall()
     }  
  },
  //如果大于 就用管道传输
  readBig(){
  		 let rs = fs.createReadStream(this.src);
      let ws = fs.createWriteStream("./big/+this.src");
    	rs.pipe(ws)
  },
  //如果小于 就读取然后覆盖式写到small文件里
  readSmall(){
  		fs.readFile(this.src,"utf-8",(error,con)=>{
        if(error) throw error;
        fs.writeFileSync("./small/"+this.src,con)
      })
  }
}
