function jumpFloor(num) {
    let p = 1,
       n = 1,
       f = 0;
    if (num <= 0) return 0;
    if (num === 1) return n;
    for (let i = 1; i < num; i++) {
        f = p + n;
        p = n;
        n = f;
    }
    return f;
}
