var FileLoader = function(file, events) {
    this.reader = new FileReader();
    this.file = file;
    this.loaded = 0;
    this.total = file.size;
    //每次读取1M
    this.step = 1024 * 1024;
    this.events = events || {};
    //读取第一块
    this.readBlob(0);
    this.bindEvent();
}
FileLoader.prototype = {
    bindEvent: function(events) {
        var _this = this,
            reader = this.reader;
        reader.onload = function(e) {
            _this.onLoad();
        };
        reader.onprogress = function(e) {
            _this.onProgress(e.loaded);
        };
        // start 、abort、error 回调暂时不加
    },
    // progress 事件回掉
    onProgress: function(loaded) {
        var percent,
            handler = this.events.progress;
        this.loaded += loaded;
        percent = (this.loaded / this.total) * 100;
        handler && handler(percent);
    },
    // 读取结束（每一次执行read结束时调用，并非整体）
    onLoad: function() {
        var handler = this.events.load;
        // 应该在这里发送读取的数据
        handler && handler(this.reader.result);
        // 如果未读取完毕继续读取
        if (this.loaded < this.total) {
            this.readBlob(this.loaded);
        } else {
            // 读取完毕
            this.loaded = this.total;
            // 如果有success回掉则执行
            this.events.success && this.events.success();
        }
    },
    // 读取文件内容
    readBlob: function(start) {
        var blob,
            file = this.file;
        // 如果支持 slice 方法，那么分步读取，不支持的话一次读取
        if (file.slice) {
            blob = file.slice(start, start + this.step);
        } else {
            blob = file;
        }
        this.reader.readAsText(blob);
    },
    // 中止读取
    abort: function() {
        var reader = this.reader;
        if (reader) {
            reader.abort();
        }
    }
}

function deep(arr, res) {   
    var res = res || [];   
    arr.forEach(function(val) { 
        if (Array.isArray(val)) {    
            deep(val, res);   
        } else {    
            res.push(val);   
        }   
    });   
    return res;  
}  
console.log(deep([1, 2, 3, [4, 5, [6, 7, [8, 9]]]]));
// [1, 2, 3, 4, 5, 6, 7, 8, 9]
console.log(deep([[1, 2],[3, 4],[[5,6],[7, 8, 9]]]))
// [1, 2, 3, 4, 5, 6, 7, 8, 9]

 function getRate(obj, oEvent) {
    // 图片地址设置
    var imgSrc = 'img/star.png'; //没有填色的星星
    var imgSrc_2 = 'img/star_full.png'; //打分后有颜色的星星,这里的star_full图片时实心的有颜色的五星。
    if (obj.rateFlag) return;
    var e = oEvent || window.event;
    var target = e.target || e.srcElement;
    var imgArray = obj.getElementsByTagName("img");
    for (var i = 0; i < imgArray.length; i++) {
        imgArray[i]._num = i;
        imgArray[i].onclick = function() {
            if (obj.rateFlag) return;
            obj.rateFlag = true;
            //this._num+1这个值写入到数据库中,作为评分的依据。可以在html页面加一个隐藏域，这里可以通过jquery把这个值赋值给隐藏域，保存的时候传给后台
        };
    }
    if (target.tagName == "IMG") {
        for (var j = 0; j < imgArray.length; j++) {
            if (j <= target._num) {
                imgArray[j].src = imgSrc_2;
            } else {
                imgArray[j].src = imgSrc;
            }
        }
    } else {
        for (var k = 0; k < imgArray.length; k++) {
            imgArray[k].src = imgSrc;
        }
    }
}
getRate(4); // ★★★★☆
getRate(4, 8); // ★★★★☆☆☆☆
getRate(3.4); //  ★★★☆☆
