let dz = path.join(__dirname,'./2mb.js');//拼接2mb.js路径

function jx(dz){//封装函数
     let xinxi = fs.statSync(dz);//得到dz的信息
     let size = xinxi.size;//得到他的size属性
     let res = size/1024/1024;//让它等于b
     console.log(res);//打印文件大小
     if(res<2){//如果 小于2
         fs.readFile(dz,(err,data)=>{//读取文件
             var newdata =  data.toString();//把内容变成字符串
             console.log(newdata);//打印内容
         });
     }else{//大于2
        console.log("大于2MB");
     }
}
jx(dz);

function getRate(fen, num = 5) {//函数体，把总分默认值设为5
    if (fen > num) {//判断如果评分大于总数
        console.log('评分大于总数');
        return;//终止运行
    } else {//如果评分小于总数
        if (fen < 0 && num <= 0) {//如果分数小于0或者总数小于等于0
            console.log("评分不能小于零，总数不能小于等于0");
            return;//终止运行
        } else {//如果分数不小于0并且总数大于0
            let fens = fen.toString();//把评分转为字符串
            let dian = fens.split(".");//把点分割字符串
            if (dian[1]) {//如果不是整数
                if (dian[1] > 4) {//如果小数点后大于四，就进一
                    let fenshu = dian[0] * 1 + 1;//把分数转成数字，并且进一
                    let strn1 = "★".repeat(fenshu);//字符串拼接评分的数量五角星
                    let strn2 = "☆".repeat(num - fenshu);//用总数减去评分的等于白色的五角星
                    return strn1 + strn2;//返回这两个字符串并拼接
                } else {//如果小数点后小于4，就舍去
                    let fenshus = dian[0];//分数就等于小数点前的数
                    let strn3 = "★".repeat(fenshus);//字符串拼接评分的数量五角星
                    let strn4 = "☆".repeat(num - fenshus);//用总数减去评分的等于白色的五角星
                    return strn3 + strn4;//返回这两个字符串并拼接
                }
            } else {//如果是整数
                let str = "★".repeat(fen);//直接拼接字符串
                let str2 = "☆".repeat(num - fen);////用总数减去评分的等于白色的五角星
                return str + str2;//返回这两个字符串并拼接
            }
        }
    }
}

console.log(getRate(4.2,5));//调用函数
