router.beforeEach((to, from, next) => {
  if (getToken()) { // determine if there has token
    // write your code here
    if（to.path=="/login）{
      return next({path: '/'});
  }
  else{
     next();
  } 
  } else {
    // write your code here
      whiteList.forEach((item,index)=>{
         if (to.path !== item) {//跳转到登录页
            return next({path: '/login'});
          }   
      })            
      next();

  }
})
