//全局守卫，记录登录状态
router.beforeEach((to,from,next)=>{
    //需求登录判断
    if(to.meta.auth){
        //未登录
        var isLogin = sessionStorage.getItem('isLogin');
        if(isLogin){
            next();
        }else{
          next('login');
        }
    }else{
      next();
    }
})
