           if (number <= 0) {
               return 0;
           }
           if (number == 1) {
               return 1;
           }
           if (number == 2) {
               return 2;
           }
           var jumpNMinusOne = 1;
           var jumpNMinusTwo = 2;
           var jumpN = 0;
           for (var i = 3; i <= number; i++) {
               jumpN = jumpNMinusOne + jumpNMinusTwo;
               jumpNMinusOne = jumpNMinusTwo;
               jumpNMinusTwo = jumpN;

           }
