function jumpFloor2(n) {
        var target = 0, number1 = 1, number2 = 2;
        if(n<=0)return 0;
        if(n == 1) return 1;
        if(n==2) return 2;
        for(var i=3;i<=n;++i) {
            target = number1 + number2;
            number1 = number2;
            number2 = target;
        }
        return target;
    }
