const fs = require('fs');
class readFile {
    constructor(url) {
        this.url = url;
        this.init()
    }
    init() {
        let size = fs.statSync(this.url).size;
        console.log(size)
        if (size > 2000) {
            this.readStream()
        } else {
            this.readSmallFile()
        }
    }
    readStream() {
        let rs = fs.createReadStream(this.url);
        let ws = fs.createWriteStream('./big/' + this.url);
        rs.pipe(ws)
    }
    readSmallFile() {
        fs.readFile(this.url, 'utf-8', (err, con) => {
            if (err) throw err;
            fs.writeFileSync('./small/' + this.url, con)
        })
    }
}
