import React, { Component } from 'react'

class Mine extends Component {
        state={
            arr:['这是第一条', '这是第二条', '这是第三条']
        }
    render() {
        let {arr}=this.state
        return (
        <div>
            {
                arr.map((v,i)=>{
                    return <div key={i} onClick={()=>{alert(i)}}>{v}</div>
                })
            }
        </div>
        )
    }
}

export default Mine
