class RouteTransition{
    //补全代码
    constructor(box,arr){
        this.box=document.querySelector(box);
        this.arr=arr;
        this.resolve=null;
        this.moveEnd();
        this.start().then(()=>{
            this.endCallback();
        });
        // this.move(arr[0].x,arr[0].y);
        // this.move(arr[1].x,arr[1].y);
        // this.move(arr[2].x,arr[2].y);
    }
    moveEnd(){
        this.box.addEventListener('transitionend',()=>{
            this.resolve&&this.resolve();
        })
    }
    move(x,y){
        return new Promise((resolve,reject)=>{
            this.box.style.transform=`translate(${x}px,${y}px)`;
            this.resolve=resolve;
        })
    }
    async start(){
        for(let val of this.arr){
            await this.move(val.x,val.y);
        }
    }
    end(ck){
        console.log('end');
        this.endCallback=ck;
    }
}
