public class Solution {
    public int JumpFloorII(int target) {
        if(target==0){
			return 0;

		}
		if(target==1){
			return 1;
		}
		return 2*JumpFloorII(target-1);
    }
}



class Solution {
public:
    int jumpFloorII(int number) {
        if(!number) return 0;
        return (number == 1) ? 1 : 2*jumpFloorII(number-1);
    }
}

