function check(str){ 
   var U = 0, D = 0, L = 0, R = 0; 
   for(var i=0; i < str.length; i++){ 
     switch (str[i]){ 
       case "U":U=U+1;break; 
       case "D":D=D+1;break; 
       case "L":L=L+1;break; 
       case "R":R=R+1;break; 
     } 
   }
   if(U==D && L==R){
  		return true
   }else{
    return false
   }
}
