function computedPrice(car){
  let res={
    group:car.map((v,i)=>{
      return {
        typeName:v.groupName,
        price:v.type.reduce((prev,next)=>prev+next.price,0)
      }
    })
  }
  res.allPrice=res.group.reduce((prev,next)=>prev+next.price,0)
  return res
}
