function computedPrice(car){
  let arr={
      allPrice:0,
      group:[]
  };
  car.map(item=>{
      arr.group.push({
          typeName:item.groupName,
          price:getprice(item.type)
      })
      arr.allPrice = getprice(arr.group);
  })
  function getprice(types){
      var result = 0;
      for(var i = 0; i < types.length; i++) {
          result += types[i].price;
      }
      return result;
  }
}
