student_answer:
let loadImg = (src)=>{
        return new Promise((resolve,reject)=>{
            let img = new Image();
                img.onload = ()=>{
                    resolve(img);
                }
                img.src = src;
        });
    }
    const imgs = ['url1', 'url2', 'url3'];

    async function asyncfn(imgs) {
        for(var i =0;i<imgs.length;i++){
            await loadImg(imgs[i])
        }
        // await loadImg(img[1])
    }
    asyncfn(imgs);

