    var selt=this
  if(!(type in selt.things)){
     selt.things[type]=[]
     }
    selt.things[type].push(things)
    return this
  }
  emit(type){
    var selt=this
  if(type){
     	var things=Array.prototype.slice.call(arguments,1)
         for(var i = 0; i < selt.things[type].length; i++) {
       selt.handlers[things][i].apply(selt,type);
