function stata(sizenum){
    // 封装一个方法适配大小文件的读取，限定值2M
    const fs = require('fs')
    let sizea = fs.statSync(sizenum).size
    // 2mb的字节
    let inf = 1024*1024*2
    if(sizea>=inf){
        return;
    }else{
        return fs.readFileSync(sizenum,'utf-8')
    }
}
