// 实现一个评星方法 说明

// 可根据传入的评分和总数，返回评星结果（用 ★ 和 ☆ 描述）
// 评分必选项，四舍五入，总数可选，大于0的整数，默认为5
// 对于评分为空或小于0、评分大于总数、
// 总数小于0或总数非整数的情况，返回’errror’ 示例
function getRate(num,z=5){
    if(num>z||Math.round(num)<=0){
        return 'errror';
    }
    else{
        var a,b;
       for(var i=0;i<Math.round(num);i++){
            return a=+'★'
        }
        for(var i=0;i<(z-Math.round(num));i++){
            return b=+'☆'
        }
        return a+b
    }
}
getRate(4); // ★★★★☆
getRate(4, 8); // ★★★★☆☆☆☆
getRate(3.4); //  ★★★☆☆
getRate(5, 2); // 'error'
getRate(-2); // 'error'
getRate(3, 5.5); // 'error'
