class QueueElement {
  constructor(element, priority) {
    this.element = element;
    this.priority = priority;
  }
}

class PriorityQueue {
  constructor() {
    this.arr = [];
  }

  print() {
    console.log(this.arr.map((item) => {
      return item.element
    }).join(" "))
  }

  enqueue(str, num) {
    let ele = new QueueElement(str, num);
    if (!this.arr.length) {
      this.arr.push(ele)
    } else {
      let adds = false;
      for (let i = 0; i < this.arr.length; i++) {
        if (ele.num < this.arr[i].num) {
          this.arr.splice(i, 0, ele);
          adds = true;
          break;
        } else if (ele.num == this.arr[i].num) {
          this.arr.splice(i + 1, 0, ele);
          adds = true;
          break;
        }
      }
      if (!adds) {
        this.arr.push(ele)
      }
    }
  }
}

