let arr = [];
function jump(num) {
  let sum = 0
  arr.forEach(item => {
    sum += item
  })
  if (sum == 9) {
    arr.push(1)
  } else if (sum < num) {
    arr.push(Math.floor(Math.random() * 2 + 1))
    jump(num)
  }
  return arr
}
