var largestPerimeter = function (array) {
    if (array == null || array.length < 3) return 0;
    var max = 0;
    var sum = 0;
    array = array.sort();

    for (var i = 2; i < array.length; i++) {
        sum = array[i] + array[i - 1] + array[i - 2];
        if (array[i] * 2 < sum)
            max = Math.max(max, sum);
    }
    return max;
}

console.log(largestPerimeter([3,2,3,4]))
