/**
 * 根据生日获取年龄
 * @param birthday
 * @returns {number}
 */
function getAgeByBirthday(birthday) {
  let birthDate = new Date(birthday);  // 生成生日日期实例
  let nowDateTime = new Date();   // 生成生日日期实例
  let age = nowDateTime.getFullYear() - birthDate.getFullYear();
  // 再考虑月、天的因素;.getMonth()获取的是从0开始的，这里进行比较，不需要加1
  if (nowDateTime.getMonth() < birthDate.getMonth() ||
    (nowDateTime.getMonth() === birthDate.getMonth() && nowDateTime.getDate() < birthDate.getDate())) {
    age--;
  }
  return age;
}
getAgeByBirthday('1995-9-15')
```
